import React from 'react'
import { HStack, IconButton, ListItem, Text, List } from '@chakra-ui/react'
import { DeleteIcon } from '@chakra-ui/icons'
import { useDispatch } from 'react-redux'
import { deleteUser } from '../redux/userSlice'
import { User } from '../redux/types'
import { RootState } from '../redux/store'
import { useSelector } from 'react-redux'
import { Todo } from '../redux/types'
import TodoItem from './TodoItem'

function UserItem(user: User) {
  const dispatch = useDispatch()
  const todos: Todo[] = useSelector((state: RootState) => state.todos.todos)
  const user_todos = user.todoIds.map((id) => todos.find((todo) => todo.id === id)).filter((todo) => todo !== undefined) as Todo[]

  const handleDelete = () => {
    dispatch(deleteUser(user.id))
  }

  return (
    <ListItem display="flex" alignItems="center" justifyContent="space-between">
      <HStack>
        <HStack>
          <Text>
            {user.text}
          </Text>
          <IconButton
            aria-label="Delete User"
            icon={<DeleteIcon />}
            onClick={handleDelete}
          />
        </HStack>
        <List>
          {user_todos.map((todo) => TodoItem(todo))}
          {/* {todos.map((todo) => TodoItem(todo))} */}
        </List>
      </HStack>
    </ListItem>
  )
}

export default UserItem
