import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { Box, Button, Container, Heading, Input, HStack, VStack, Select } from '@chakra-ui/react'
import { AddIcon } from '@chakra-ui/icons'
import { addTodo } from './redux/todoSlice'
import { addUser } from './redux/userSlice'
import TodoList from './components/TodoList'
import UserList from './components/UserList'

function App() {
  const [inputTodo, setInputTodo] = useState('')
  const [inputUser, setInputUser] = useState('')
  const dispatch = useDispatch()

  const handleChangeTodo = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputTodo(event.target.value)
  }

  const handleSubmitTodo = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()
    if (inputTodo.trim() !== '') {
      dispatch(addTodo(inputTodo))
      setInputTodo('')
    }
  }

  const handleChangeUser = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputUser(event.target.value)
  }

  const handleSubmitUser = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()
    if (inputUser.trim() !== '') {
      dispatch(addUser(inputUser))
      setInputUser('')
    }
  }

  return (

    <Container maxW="sm">
      <Select placeholder="選択してください">
        <option value="option1">オプション1</option>
        <option value="option2">オプション2</option>
        <option value="option3">オプション3</option>
      </Select>
      <HStack>
        <VStack spacing={4} mt={8}>
          <Heading as="h1" size="lg">
            Todoリスト
          </Heading>
          <form onSubmit={handleSubmitTodo}>
            <Box display="flex">
              <Input
                type="text"
                value={inputTodo}
                onChange={handleChangeTodo}
                mr={2}
              />
              <Button type="submit" leftIcon={<AddIcon />} colorScheme="teal">
                追加
              </Button>
            </Box>
          </form>
          <TodoList />
        </VStack>
        <VStack spacing={4} mt={8}>
          <Heading as="h1" size="lg">
            Userリスト
          </Heading>
          <form onSubmit={handleSubmitUser}>
            <Box display="flex">
              <Input
                type="text"
                value={inputUser}
                onChange={handleChangeUser}
                mr={2}
              />
              <Button type="submit" leftIcon={<AddIcon />} colorScheme="teal"></Button>
            </Box>
          </form>
          <UserList />
        </VStack>
      </HStack>
    </Container>
  )
}

export default App
