import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { Box, Button, Container, Heading, Input, HStack, VStack } from '@chakra-ui/react'
import { AddIcon } from '@chakra-ui/icons'
import { addUser } from './redux/userSlice'
import TodoList from './components/TodoList'
import UserList from './components/UserList'

// const select = ['Todo', 'User']

function App() {
  const [inputUser, setInputUser] = useState('')
  const dispatch = useDispatch()

  const handleChangeUser = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputUser(event.target.value)
  }

  const handleSubmitUser = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()
    if (inputUser.trim() !== '') {
      dispatch(addUser(inputUser))
      setInputUser('')
    }
  }

  return (

    <Container maxW="sm">
      <HStack>
        <TodoList />
        <VStack spacing={4} mt={8}>
          <Heading as="h1" size="lg">
            Userリスト
          </Heading>
          <form onSubmit={handleSubmitUser}>
            <Box display="flex">
              <Input
                type="text"
                value={inputUser}
                onChange={handleChangeUser}
                mr={2}
              />
              <Button type="submit" leftIcon={<AddIcon />} colorScheme="teal"></Button>
            </Box>
          </form>
          <UserList />
        </VStack>
      </HStack>
    </Container>
  )
}

export default App
