import React, { useState } from 'react'
import { HStack, IconButton, ListItem, Text, List, VStack } from '@chakra-ui/react'
import { DeleteIcon } from '@chakra-ui/icons'
import { useDispatch } from 'react-redux'
import { deleteUser } from '../redux/userSlice'
import { User } from '../redux/types'
import { RootState } from '../redux/store'
import { useSelector } from 'react-redux'
import { Todo } from '../redux/types'
import TodoItem from './TodoItem'

function UserItem(user: User) {
  const [inputUser, setInputUser] = useState('')
  const dispatch = useDispatch()
  const todos: Todo[] = useSelector((state: RootState) => state.todos.todos)
  const user_todos = user.todoIds.map((id) => todos.find((todo) => todo.id === id)).filter((todo) => todo !== undefined) as Todo[]

  const handleDelete = () => {
    dispatch(deleteUser(user.id))
  }

  const handleChangeUser = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputUser(event.target.value)
  }

  const handleSubmitUser = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()
    if (inputUser.trim() !== '') {
      dispatch(addUser(inputUser))
      setInputUser('')
    }
  }

  return (
    <ListItem display="flex" alignItems="center" justifyContent="space-between">
      <VStack>
        <HStack>
          <form onSubmit={handleSubmitUser}>
            <Box display="flex">
              <Input
                type="text"
                value={inputUser}
                onChange={handleChangeUser}
                mr={2}
              />
              <Button type="submit" leftIcon={<AddIcon />} colorScheme="teal"></Button>
            </Box>
          </form>
          <Text>
            {user.text}
          </Text>
          <IconButton
            aria-label="Delete User"
            icon={<DeleteIcon />}
            onClick={handleDelete}
          />
          <List>
            {user_todos.map((todo) => TodoItem(todo))}
            {/* {todos.map((todo) => TodoItem(todo))} */}
          </List>
        </HStack>
      </VStack>
    </ListItem>
  )
}

export default UserItem
